(270, 1, '2024-06-26 03:45:53', '2024-06-26 03:45:53', '\r\n// Function to retrieve posts based on category and date filter\r\nfunction get_filtered_posts($category_name, $date_filter) {\r\n    $args = array(\r\n        \'post_status\' => \'publish\',\r\n        \'category_name\' => $category_name,\r\n        \'posts_per_page\' => -1\r\n    );\r\n\r\n    // Filter berdasarkan tanggal\r\n    switch ($date_filter) {\r\n        case \'24hours\':\r\n            $args[\'date_query\'] = array(\r\n                array(\r\n                    \'after\' => \'1 day ago\',\r\n                    \'inclusive\' => true\r\n                )\r\n            );\r\n            break;\r\n        case \'week\':\r\n            $args[\'date_query\'] = array(\r\n                array(\r\n                    \'after\' => \'1 week ago\',\r\n                    \'inclusive\' => true\r\n                )\r\n            );\r\n            break;\r\n        case \'month\':\r\n            $args[\'date_query\'] = array(\r\n                array(\r\n                    \'after\' => \'1 month ago\',\r\n                    \'inclusive\' => true\r\n                )\r\n            );\r\n            break;\r\n    }\r\n\r\n    return new WP_Query($args);\r\n}\r\n\r\n// Get the selected date filter for each section from query params\r\n$agenda_date_filter = isset($_GET[\'agenda_date_filter\']) ? $_GET[\'agenda_date_filter\'] : \'any\';\r\n$pengumuman_date_filter = isset($_GET[\'pengumuman_date_filter\']) ? $_GET[\'pengumuman_date_filter\'] : \'any\';\r\n?>\r\n\r\n<main class=\"background-warna3\">\r\n  <!-- Section Agenda -->\r\n  <div id=\"agenda\" class=\"agenda category\">\r\n    <div class=\"title\">\r\n      <div class=\"text\">Agenda</div>\r\n    </div>\r\n    <div class=\"tags\">\r\n      <div class=\"tag\" data-filter=\"agenda\" data-date-filter=\"any\">Any time</div>\r\n      <div class=\"tag\" data-filter=\"agenda\" data-date-filter=\"24hours\">Post 24 hours</div>\r\n      <div class=\"tag\" data-filter=\"agenda\" data-date-filter=\"week\">Post week</div>\r\n      <div class=\"tag\" data-filter=\"agenda\" data-date-filter=\"month\">Post month</div>\r\n    </div>\r\n    <div class=\'contents\'>\r\n      <?php\r\n      $agenda = get_filtered_posts(\'agenda\', $agenda_date_filter);\r\n      if ($agenda->have_posts()) :\r\n        while ($agenda->have_posts()) : $agenda->the_post(); ?>\r\n          <a href=\"<?php the_permalink(); ?>\" class=\"content\">\r\n            <div class=\"title\"><?php the_title(); ?></div>\r\n            <div class=\"date\"><i class=\"fa-regular fa-calendar-days\"></i><?php the_time(\'l, j F, Y\'); ?></div>\r\n          </a>\r\n      <?php\r\n        endwhile;\r\n        wp_reset_postdata();\r\n      else :\r\n        echo \'<p>No posts found.</p>\';\r\n      endif;\r\n      ?>\r\n    </div>\r\n  </div>\r\n\r\n  <!-- Section Pengumuman -->\r\n  <div id=\"pengumuman\" class=\"pengumuman category\">\r\n    <div class=\"title\">\r\n      <div class=\"text\">Pengumuman</div>\r\n    </div>\r\n    <div class=\"tags\">\r\n      <div class=\"tag\" data-filter=\"pengumuman\" data-date-filter=\"any\">Any time</div>\r\n      <div class=\"tag\" data-filter=\"pengumuman\" data-date-filter=\"24hours\">Post 24 hours</div>\r\n      <div class=\"tag\" data-filter=\"pengumuman\" data-date-filter=\"week\">Post week</div>\r\n      <div class=\"tag\" data-filter=\"pengumuman\" data-date-filter=\"month\">Post month</div>\r\n    </div>\r\n    <div class=\"contents\">\r\n      <?php\r\n      $pengumuman = get_filtered_posts(\'pengumuman\', $pengumuman_date_filter);\r\n      if ($pengumuman->have_posts()) :\r\n        while ($pengumuman->have_posts()) : $pengumuman->the_post(); ?>\r\n          <a href=\"<?php the_permalink(); ?>\" class=\"content\">\r\n            <div class=\"image zoom\"><?php the_post_thumbnail(); ?></div>\r\n            <div class=\"text\">\r\n              <div class=\"desc\"><?php the_title(); ?></div>\r\n              <div class=\"date\"><i class=\"fa-regular fa-calendar-days\"></i><?php the_time(\'l, j F, Y\'); ?></div>\r\n            </div>\r\n          </a>\r\n      <?php\r\n        endwhile;\r\n        wp_reset_postdata();\r\n      else :\r\n        echo \'<p>No posts found.</p>\';\r\n      endif;\r\n      ?>\r\n    </div>\r\n  </div>\r\n</main>\r\n\r\n<script>\r\n  // Handle date filter click events and update URL parameters\r\n  document.querySelectorAll(\'.tag\').forEach((tag) => {\r\n    tag.addEventListener(\'click\', (e) => {\r\n      const filterType = e.target.dataset.filter;\r\n      const dateFilter = e.target.dataset.dateFilter;\r\n      const urlParams = new URLSearchParams(window.location.search);\r\n\r\n      // Set date filter based on the clicked section (agenda or pengumuman)\r\n      urlParams.set(`${filterType}_date_filter`, dateFilter);\r\n      window.location.search = urlParams.toString();\r\n    });\r\n  });\r\n</script>\r\n', 'Agenda & Pengumuman', '', 'publish', 'closed', 'closed', '', 'agenda-pengumuman', '', '', '2024-10-04 07:36:21', '2024-10-04 07:36:21', '', 0, 'http://localhost/wordpress/wpcode/agenda-pengumuman/', 0, 'wpcode', '', 0),
