(372, 1, '2024-06-27 08:16:12', '2024-06-27 08:16:12', '		  $artikel = new wp_Query(array(\r\n    \'post_status\' => \'publish\',\r\n    \'category_name\' => \'artikel\',\r\n  	\'posts_per_page\' => -1,\r\n    ));\r\n			  ?>\r\n	<main>\r\n        <div class=\"artikel category\">\r\n            <div class=\"title\">\r\n                <div class=\"text\">Artikel</div>\r\n            </div>\r\n			 <div class=\"tags\">\r\n      <div class=\"tag\" data-date-filter=\"any\">any time</div>\r\n      <div class=\"tag\" data-date-filter=\"24hours\">post 24 hours</div>\r\n      <div class=\"tag\" data-date-filter=\"week\">post week</div>\r\n      <div class=\"tag\" data-date-filter=\"month\">post month</div>\r\n    </div>\r\n            <div class=\"contents\">\r\n                <?php\r\n				// Set default date filter to \"any\"\r\n      $date_filter = \'any\';\r\n\r\n      // Check if a date filter is selected\r\n      if (isset($_GET[\'date_filter\'])) {\r\n        $date_filter = $_GET[\'date_filter\'];\r\n      }\r\n\r\n      // Set up query arguments based on date filter\r\n      $args = array(\r\n        \'post_type\' => \'post\',\r\n        \'posts_per_page\' => -1\r\n      );\r\n\r\n      switch ($date_filter) {\r\n        case \'24hours\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 day ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          break;\r\n        case \'week\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 week ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          break;\r\n        case \'month\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 month ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          break;\r\n        default:\r\n          // No date filter\r\n          break;\r\n      }\r\n                // Loop melalui hasil query\r\n                if ($artikel->have_posts()) :\r\n                    while ($artikel->have_posts()) :\r\n                        $artikel->the_post();\r\n                ?>\r\n                        <a href=\"<?php the_permalink(); ?>\" class=\"content\">\r\n                            <div class=\"image\"><?php the_post_thumbnail()?></div>\r\n							<div class=\"text\">\r\n								<div class=\"text-category\">School News</div>\r\n                            	<div class=\"desc\"><?php the_title(); ?></div>\r\n                          	  <div class=\"date\"><i class=\"fa-regular fa-calendar-days\"></i><?php the_time( \'j F, Y\' ); ?></div>\r\n							</div>\r\n                        </a>\r\n\r\n                <?php\r\n                    endwhile;\r\n                    wp_reset_postdata(); // Mengatur ulang data posting\r\n                else :\r\n                    // Tidak ada posting yang sesuai dengan kategori yang ditemukan\r\n                    echo $no_post;\r\n                endif;			?>\r\n				</div>\r\n			</div>\r\n		</main>\r\n<script>\r\n  const tags = document.querySelectorAll(\'.tag\');\r\n  const contents = document.querySelectorAll(\'.contents\');\r\n\r\n  tags.forEach((tag) => {\r\n    tag.addEventListener(\'click\', (e) => {\r\n      const dateFilter = e.target.dataset.dateFilter;\r\n      const urlParams = new URLSearchParams(window.location.search);\r\n      urlParams.set(\'date_filter\', dateFilter);\r\n      window.location.search = urlParams.toString();\r\n    });\r\n  });\r\n</script>', 'Full Artikel', '', 'publish', 'closed', 'closed', '', 'full-artikel', '', '', '2024-06-27 08:20:27', '2024-06-27 08:20:27', '', 0, 'http://localhost/wordpress/wpcode/full-artikel/', 0, 'wpcode', '', 0),
