(92, 1, '2024-05-13 09:13:56', '0000-00-00 00:00:00', 'add_action(\'admin_init\', function () {\r\n    // Redirect any user trying to access comments page\r\n    global $pagenow;\r\n    \r\n    if ($pagenow === \'edit-comments.php\') {\r\n        wp_safe_redirect(admin_url());\r\n        exit;\r\n    }\r\n\r\n    // Remove comments metabox from dashboard\r\n    remove_meta_box(\'dashboard_recent_comments\', \'dashboard\', \'normal\');\r\n\r\n    // Disable support for comments and trackbacks in post types\r\n    foreach (get_post_types() as $post_type) {\r\n        if (post_type_supports($post_type, \'comments\')) {\r\n            remove_post_type_support($post_type, \'comments\');\r\n            remove_post_type_support($post_type, \'trackbacks\');\r\n        }\r\n    }\r\n});\r\n\r\n// Close comments on the front-end\r\nadd_filter(\'comments_open\', \'__return_false\', 20, 2);\r\nadd_filter(\'pings_open\', \'__return_false\', 20, 2);\r\n\r\n// Hide existing comments\r\nadd_filter(\'comments_array\', \'__return_empty_array\', 10, 2);\r\n\r\n// Remove comments page in menu\r\nadd_action(\'admin_menu\', function () {\r\n    remove_menu_page(\'edit-comments.php\');\r\n});\r\n\r\n// Remove comments links from admin bar\r\nadd_action(\'init\', function () {\r\n    if (is_admin_bar_showing()) {\r\n        remove_action(\'admin_bar_menu\', \'wp_admin_bar_comments_menu\', 60);\r\n    }\r\n});', 'Completely Disable Comments', '', 'draft', 'closed', 'closed', '', '', '', '', '2024-05-13 09:13:56', '0000-00-00 00:00:00', '', 0, 'http://localhost/wordpress/?post_type=wpcode&p=92', 0, 'wpcode', '', 0),
(93, 1, '2024-05-13 09:18:35', '2024-05-13 09:18:35', '<div class=\"container beranda\">\r\n<header>\r\n<div class=\"hero\">\r\n<div class=\"text\">\r\n<h2 class=\"title\">SMK Negeri 1 Kota Bekasi Cerdas Mengembangkan Teknologi!</h2>\r\n<p>SMK Negeri 1 Kota Bekasi merupakan sekolah yang ditunjuk oleh Direktorat Pembinaan SMK (PSMK) sebagai Rintisan Sekolah Bertaraf Internasional untuk seluruh kompetensi keahlian sejak tahun 2008.</p>\r\n<div class=\"info\">\r\n<div>\r\n<div class=\"number\">8</div>\r\n<div class=\"detail\">Competition Programs</div>\r\n</div>\r\n<div>\r\n<div class=\"number\">10+</div>\r\n<div class=\"detail\">Extraculicular Programs</div>\r\n</div>\r\n<div>\r\n<div class=\"number\">20+</div>\r\n<div class=\"detail\">Industry Colaborations</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"company-provile\"><img class=\"background\" src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/Hero-Ilustration-1.png\" alt=\"hero image\" /></div>\r\n</div>\r\n<div class=\"logo-company\"><img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/acer-logo-300x150-1.png\" alt=\"logo acer\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/bank-btn-logo-1024x237-1.png\" alt=\"logo bank btn\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/logo-bank-dki-1024x576-1.webp\" alt=\"bank dki\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/logo-toyota-150x150-1.jpg\" alt=\"toyota\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/logo-astra-internasional.jpg\" alt=\"astra Internasional\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/bmkg-150x150-1.png\" alt=\"astra Internasional\" /> <img src=\"http://localhost/wp-smkn1-web/wordpress/wp-content/uploads/2023/09/bpn.png\" alt=\"astra Internasional\" /></div>\r\n<p><!-- versi dicky--> <!-- \r\n\r\n<div class=\"logo-compa