(270, 1, '2024-06-26 03:45:53', '2024-06-26 03:45:53', '$agenda = new wp_Query(array(\r\n    \'post_status\' => \'publish\',\r\n    \'category_name\' => \'agenda\',\r\n    ));\r\n$pengumuman = new wp_Query(array(\r\n    \'post_status\' => \'publish\',\r\n    \'category_name\' => \'pengumuman\',\r\n    ));\r\n?>\r\n\r\n<main class=\"background-warna3\">\r\n  <div id=\"agenda\" class=\"agenda category\">\r\n    <div class=\"title\"> \r\n      <div class=\"text\">Agenda</div>\r\n    </div>\r\n    <div class=\"tags\">\r\n      <div class=\"tag\" data-date-filter=\"any\">any time</div>\r\n      <div class=\"tag\" data-date-filter=\"24hours\">post 24 hours</div>\r\n      <div class=\"tag\" data-date-filter=\"week\">post week</div>\r\n      <div class=\"tag\" data-date-filter=\"month\">post month</div>\r\n    </div>\r\n    <div class=\'contents\'>\r\n      <?php\r\n      // Set default date filter to \"any\"\r\n      $date_filter = \'any\';\r\n\r\n      // Check if a date filter is selected\r\n      if (isset($_GET[\'date_filter\'])) {\r\n        $date_filter = $_GET[\'date_filter\'];\r\n      }\r\n\r\n      // Set up query arguments based on date filter\r\n      $args = array(\r\n        \'post_type\' => \'post\',\r\n        \'posts_per_page\' => -1\r\n      );\r\n\r\n      switch ($date_filter) {\r\n        case \'24hours\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 day ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          break;\r\n        case \'week\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 week ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          break;\r\n        case \'month\':\r\n          $args[\'date_query\'] = array(\r\n            array(\r\n              \'after\' => \'1 month ago\',\r\n              \'inclusive\' => true\r\n            )\r\n          );\r\n          